mapper.select:
select distinct rm.menu_id from sys_user_role ur 
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{userId}

SELECT su1.user_id as userId, su1.username, ti.title FROM sys_user su1 LEFT JOIN sys_title ti ON su1.title = ti.id WHERE
and su1.title > #{titlename}
and su1.username like '%${username}%'
and su1.id = #{id}
order by su1.title
